                <NavDropdown.Item href="/skippedList">
                  skipped
                </NavDropdown.Item>
                <NavDropdown.Item href="/solvedList">solved</NavDropdown.Item>
              </NavDropdown>

            </Nav>
            <Nav>
              <Nav.Link onClick={logout}>Logout</Nav.Link>
            </Nav>
          </Container>
        </Navbar>
      </header>
    )
  }
