import { Link } from "react-router-dom"
import { useEffect, useContext } from "react"
import { UserContext } from "./UserContext";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';

export default function Header() {
  const {userInfo, setUserInfo} = useContext(UserContext);

  useEffect(() => {
    fetch('http://localhost:4000/profile', {
      method: 'GET',
      credentials: 'include',
    })
      .then(response => {
        return response.json()
      })
      .then(data => {
        setUserInfo(data.username);
      })
  }, []);

  function logout() {
    fetch('http://localhost:4000/logout', {
      method: 'POST',
      credentials: 'include'
    })
    setUserInfo(null);
  }

  return (
    <header>
      <Navbar bg="white" data-bs-theme="light">
        <Container>
          <Navbar.Brand href="/">Codeforces</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="/addproblem">Add problem</Nav.Link>
            <NavDropdown title="Problem list" id="basic-nav-dropdown">
              <NavDropdown.Item href="/todoList">todo</NavDropdown.Item>
              <NavDropdown.Item href="/skippedList">
                skipped
              </NavDropdown.Item>
              <NavDropdown.Item href="/solvedList">solved</NavDropdown.Item>
            </NavDropdown>

          </Nav>
        </Container>
      </Navbar>
    </header>
